using namespace std;

// if s1 and s2 varies exactly 1 character
bool isNeighbor(const string& s1, const string& s2){
    int error = 0;
    for(int i=0; i<s1.size(); ++i){
        if(s1[i]!=s2[i]){
            if(++error>=2) return false;
        }
    }
    if(error==1) return true;
    return false;
}

void backtracking(vector<vector<string>>& ans, vector<string>& ret, unordered_map<string, int>& umap, string& s, int len, string& endWord, int depth, vector<vector<string>>& bucket){
    int val = umap[s];
    umap[s] = 0;
    ret.emplace_back(s);
    if(ret.size()==len-1 && s == endWord){
        for(auto& i: ret) cout<< i<<"";cout<<endl;
        ans.emplace_back(ret);
    }else if(ret.size()<len){
        //enqueue all the valid neighbor of q
        for(auto& t: bucket[depth+1]){
            if(isNeighbor(t, s)){
                backtracking(ans, ret, umap, t, len, endWord, depth+1, bucket);
            }
        }
    }
    umap[s] = val;
    ret.pop_back();

}

void expand(unordered_map<string, int>& umap, queue<string>& q, int queueIndex, int& len){
    int num = (int)q.size();
    while(num--){
        string s = q.front();
        q.pop();
        //enqueue all the valid neighbor of q
        for(int i=0; i<s.size(); ++i){
            for(char c = 'a'; c<='z'; ++c){
                string tmp = s;
                tmp[i] = c;
                if(umap.find(tmp)!=umap.end()){
                    if(umap[tmp]==0){
                        umap[tmp] = umap[s]+queueIndex;
                        q.push(tmp);
                    }else if(umap[tmp]*queueIndex>0){
                        continue;
                    }else{
                        if(len==-1)
                            len = abs(umap[s]-umap[tmp])+1;
                    }
                }
            }
        }
    }
}


vector<vector<string>> findLadders(string beginWord, string endWord, vector<string>& wordList) {
    vector<vector<string>> ans;
    vector<string> ret;
    unordered_map<string, int> umap;
    // insert all the elements in to unordered_map
    for(auto& s: wordList){
        umap[s] = 0;
    }
    umap[beginWord]=1;
    if(umap.find(endWord)==umap.end()){
        return ans;
    }else{
        if(beginWord==endWord){
            ans.emplace_back(vector<string>{beginWord});
            return ans;
        }
        umap[endWord]=-1;
    }
    queue<string> q0;
    queue<string> q1;
    q0.push(beginWord);
    q1.push(endWord);
    int len  = -1;
    while(!q0.empty() && !q1.empty() && len==-1){
        if(q0.size()<=q1.size()){
            expand(umap, q0, 1, len);
        }else{
            expand(umap, q1, -1, len);
        }
    }
    if(len==-1){
        return ans;
    }
    // construct the word path using backtracking
    for(auto& i: umap){
        if(i.second<0){
            i.second += len;
        }
    }
    vector<vector<string>> bucket(520);
    for(auto& i: umap){
        bucket[i.second].push_back(i.first);
    }

    backtracking(ans, ret, umap, beginWord, len, endWord, 1, bucket);

    return ans;
}


