#include <iostream>
#include <vector>
#include <queue>
using namespace std;

void dfs(vector<vector<int>>& grid, queue<vector<int>>& q, int r, int c){
    if(r>=0 && r<grid.size() && c>=0 && c<grid[0].size() && grid[r][c]==1){
        grid[r][c] = 2;
        q.push({r, c, 0});
        dfs(grid, q, r-1, c);
        dfs(grid, q, r+1, c);
        dfs(grid, q, r, c-1);
        dfs(grid, q, r, c+1);
    }
}

int shortestBridge(vector<vector<int>>& grid) {
    int dis;
    queue<vector<int>> q;
    int r=-1, c=-1;
    // find the first island, mark all its cell 2
    for(int i=0; i<grid.size(); ++i){
        for(int j=0; j<grid[0].size(); ++j){
            if(grid[i][j]==1){
                r=i; c = j; break;
            }
        }
        if(r!=-1 && c!=-1) break;
    }
    dfs(grid, q, r, c);

    vector<vector<bool>> visited(grid.size(), vector<bool>(grid[0].size(), false));
    while(!q.empty()){
        r = q.front()[0]; c = q.front()[1]; dis = q.front()[2];
        q.pop();
        if(r-1>=0){
            if(grid[r-1][c]==0 && !visited[r-1][c]){q.push({r-1, c, dis+1}); visited[r-1][c]=true; }
            else if(grid[r-1][c]==1) return dis;
        }
        if(r+1<grid.size()){
            if(grid[r+1][c]==0 && !visited[r+1][c]) {q.push({r+1, c, dis+1}); visited[r+1][c]=true; }
            else if(grid[r+1][c]==1) return dis;
        }
        if(c-1>=0){
            if(grid[r][c-1]==0 && !visited[r][c-1]) {q.push({r, c-1, dis+1}); visited[r][c-1]=true; }
            else if(grid[r][c-1]==1) return dis;
        }
        if(c+1<grid[0].size() ) {
            if(grid[r][c+1]==0 && !visited[r][c+1]) {q.push({r, c+1, dis+1}); visited[r][c+1]=true; }
            else if(grid[r][c+1]==1) return dis;
        }
    }
    return dis;
}


